import json

def grade_system(scores):
    avg_score = sum(scores) / len(scores)
    max_score = max(scores)
    min_score = min(scores)

    result = {
        'average_score': avg_score,
        'highest_score': max_score,
        'lowest_score': min_score
    }

    return json.dumps(result)
