import json

def grade_students(scores):
    grading_scale = {
        'A': range(90, 101),
        'B': range(80, 90),
        'C': range(70, 80),
        'D': range(60, 70),
        'F': range(0, 60)
    }
    grades = {}
    for student, score in scores.items():
        for grade, grade_range in grading_scale.items():
            if score in grade_range:
                grades[student] = grade
                break
    return json.dumps(grades)

# Example usage:
scores = {'Alice': 85, 'Bob': 92, 'Charlie': 78, 'David': 65}
json_string = grade_students(scores)
print(json_string)
