def grade_system(scores):
    average_score = sum(scores) / len(scores)
    minimum_score = min(scores)
    maximum_score = max(scores)
    grade_dict = {}
    for score in scores:
        if score >= 90:
            grade_dict[score] = 'A'
        elif score >= 80:
            grade_dict[score] = 'B'
        elif score >= 70:
            grade_dict[score] = 'C'
        elif score >= 60:
            grade_dict[score] = 'D'
        else:
            grade_dict[score] = 'F'
    return {
        'average_score': average_score,
        'minimum_score': minimum_score,
        'maximum_score': maximum_score,
        'grades': grade_dict
    }
