def grade_students(scores):
    grades = {}
    for score in scores:
        if score >= 90:
            grades[score] = 'A'
        elif score >= 80:
            grades[score] = 'B'
        elif score >= 70:
            grades[score] = 'C'
        elif score >= 60:
            grades[score] = 'D'
        else:
            grades[score] = 'F'
    return grades

# Example usage:
scores = [78, 92, 85, 60, 75]
print(grade_students(scores))
