import json


def grading_system(score):
    if score >= 90:
        return 'A'
    elif score >= 80:
        return 'B'
    elif score >= 70:
        return 'C'
    elif score >= 60:
        return 'D'
    else:
        return 'F'


grades = {
    'John': grading_system(85),
    'Alice': grading_system(92),
    'Bob': grading_system(73)
}

json_string = json.dumps(grades)
print(json_string)
