def get_grade(score):
    if score >= 90:
        return 'A'
    elif score >= 80:
        return 'B'
    elif score >= 70:
        return 'C'
    elif score >= 60:
        return 'D'
    else:
        return 'F'

# Test the function
print(get_grade(85))  # Output: B
print(get_grade(72))  # Output: C
print(get_grade(95))  # Output: A
