def fibonacci_sequence(n):
    sequence = [0, 1]
    while sequence[-1] < n:
        next_num = sequence[-1] + sequence[-2]
        if next_num < n:
            sequence.append(next_num)
        else:
            break
    return sequence

n = int(input('Enter a number: '))
print(fibonacci_sequence(n))
