def solution(s: str) -> int:
    stack = []
    opening = set(['(', '[', '{'])
    closing = set([')', ']', '}'])
    pairs = {')': '(', ']': '[', '}': '{'}

    for char in s:
        if char in opening:
            stack.append(char)
        elif char in closing:
            if not stack or stack.pop() != pairs[char]:
                return 0

    return 1 if not stack else 0
