def solution(n: int, breed: str) -> int:
    breeds = breed.split()
    breed_count = {}
    total_delegations = 0

    # Count the number of each breed
    for b in breeds:
        breed_count[b] = breed_count.get(b, 0) + 1

    # Calculate the number of possible delegations
    for i in range(n):
        count_i = breed_count[breeds[i]]
        for j in range(i+1, n):
            count_j = breed_count[breeds[j]]
            total_delegations += count_i * count_j

    return total_delegations
