def solution(s:str) -> int:
    stack = []
    for char in s:
        if char == '(' or char == '[' or char == '{':
            stack.append(char)
        elif char == ')' and (not stack or stack[-1] != '('):
            return 0
        elif char == ']' and (not stack or stack[-1] != '['):
            return 0
        elif char == '}' and (not stack or stack[-1] != '{'):
            return 0
        elif char == ')' or char == ']' or char == '}':
            stack.pop()
    if not stack:
        return 1
    else:
        return 0
