def solution(cards: list[int]) -> int:
    n = len(cards)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = cards[i]

    for l in range(2, n + 1):
        for i in range(n - l + 1):
            j = i + l - 1
            dp[i][j] = max(cards[i] + min(dp[i + 2][j], dp[i + 1][j - 1]),
                          cards[j] + min(dp[i + 1][j - 1], dp[i][j - 2]))

    return dp[0][n - 1]
