def solution(n: int, breed: str) -> int:
    breed_count = {}
    for b in breed.split():
        breed_count[b] = breed_count.get(b, 0) + 1
    total_delegations = 0
    for i in range(n - 2):
        for j in range(i + 2, n):
            if breed[i] != breed[j]:
                total_delegations += breed_count[breed[i]] * breed_count[breed[j]]
    return total_delegations
