def solution(equation: str) -> str:
    left, right = equation.split('=')
    left_molecules = left.split('+')
    right_molecules = right.split('+')
    left_counts = {}
    right_counts = {}

    # Count atoms on the left side
    for molecule in left_molecules:
        count, atoms = molecule.split('\n')
        count = int(count)
        for atom in atoms:
            if atom.isupper():
                left_counts[atom] = left_counts.get(atom, 0) + count
            else:
                left_counts[atoms[-1]] = left_counts.get(atoms[-1], 0) + count

    # Count atoms on the right side
    for molecule in right_molecules:
        count, atoms = molecule.split('\n')
        count = int(count)
        for atom in atoms:
            if atom.isupper():
                right_counts[atom] = right_counts.get(atom, 0) + count
            else:
                right_counts[atoms[-1]] = right_counts.get(atoms[-1], 0) + count

    # Check if the counts are equal
    if left_counts == right_counts:
        return 'yes'
    else:
        return 'no'
